class Solution {
    public int[] shortestToChar(String s, char c) {
        int n = s.length();
        int distance = n + 1;

        // 统计s[i]距离左边c的距离
        int[] minDistance = new int[n]; // SC O(n)
        for (int i=0; i<n; i++) { // TC O(n)
            distance = s.charAt(i) == c ? 0 : distance + 1;
            minDistance[i] = distance;
        }

        // 统计s[i]距离右边c的距离, 并且求左右两侧最小
        distance = n + 1;
        for (int i=n-1; i>=0; i--) { // TC O(n)
            distance = s.charAt(i) == c ? 0 : distance + 1;
            minDistance[i] = Math.min(minDistance[i], distance);
        }

        return minDistance;
    }
}
