class CustomStack {
    Deque<Integer> stack; // SC O(n)
    int maxSize;

    // 用增量数数组记录增量，出栈时加到栈顶元素
    int[] incr; // SC O(n)

    public CustomStack(int maxSize) {
        this.stack = new LinkedList<>();
        this.maxSize = maxSize;
        incr = new int[maxSize + 1];
    }
    
    public void push(int x) {
        if (stack.size() == maxSize) {
            return;
        }

        stack.push(x); // TC O(1)
        
    }
    
    public int pop() {
        int size = stack.size();
        if (size == 0) {
            return -1;
        }

        // stack内容如下:
        // [va1, val2, val3, val4]
        //  ^                 ^
        // bottom             top

        // incr数组内容如下:
        // [ 0,  0,   incr_val, 0]
        //   ^           ^
        // not_used    incr[2] = incr_val，代表stack底部的2个元素都会增加incr_val

        int top = stack.pop();
        int incr_val = incr[size];

        if (incr_val != 0) {
            top += incr_val;

            incr[size - 1] += incr_val;
            incr[size] = 0;
        }


        return top; // TC O(1)
    }
    
    public void increment(int k, int val) { // TC O(1)
        int total = Math.min(k, stack.size());
        incr[total] += val;
    }
}

