class Solution {
    public int maxChunksToSorted(int[] arr) {
        // 对于位置i，
        // 如果[0...i]的最大值(记为maxFromLeft) 小于等于 [i+1...n-1]的最小值(记为minFromRight)，说明此时可以分块，不影响整体排序
        // 否则就不能排序，因为maxFromLeft > minFromRight时，maxFromLeft在排序后必须挪到右侧

        int n = arr.length;
        int[] maxFromLeft = new int[n];
        int[] minFromRight = new int[n+1];

        maxFromLeft[0] = arr[0];
        for (int i=1; i<n; i++) {
            maxFromLeft[i] = Math.max(maxFromLeft[i-1], arr[i]);
        }

        minFromRight[n] = 0x3f3f3f;
        for (int i=n-1; i>=0; i--) {
            minFromRight[i] = Math.min(minFromRight[i+1], arr[i]);
        }

        int count = 1;
        for (int i=0; i<n-1; i++) {
            if (maxFromLeft[i] <= minFromRight[i+1]) {
                count++;
            }
        }

        return count;
    }
}
