/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if (head == null) {
            return null;
        }

        // 第一步， k = k % n 
        // 第二步，把倒数k个节点挪到链表的最前面
        int count = 0;
        ListNode p = head;
        while (p != null) {
            count++;
            p = p.next;
        }

        k = k % count;
        if (k == 0) {
            return head;
        }

        p = head;
        ListNode p2 = head;
        ListNode tail = null;
        while (p != null) {
            // k = 2;
            // 1 -> 2 -> 3 -> 4 -> 5 -> null
            //      ^         ^  
            //     p2         p         // when k < 0;
            //           ^        ^
            //          p2        p     // last loop            

            if (k >= 0) {
                k--;
            } else {
                p2 = p2.next;
            }

            p = p.next;
            if (p != null && p.next == null) {
                tail = p;
            }
        }

        ListNode newHead = p2.next;
        p2.next = null;
        tail.next = head;
        return newHead;
    }
}
