/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode sortedListToBST(ListNode head) {
        return dfs(head, null);
    }

    // build tree from the list [start, end) => (not including end node)
    // SC O(n)  TC: tree node -> O(n), stack size -> O(h)
    public TreeNode dfs(ListNode start, ListNode end) {
        if (start == end) {
            return null;
        }

        if (start.next == end) {
            // not including List end node, only return the tree node from the List start node.
            return new TreeNode(start.val);
        }

        // find mid
        ListNode fast = start;
        ListNode slow = start;
        while (fast != end && fast.next != end) {
            fast = fast.next.next;
            slow = slow.next;
        }

        ListNode mid = slow;
        TreeNode node = new TreeNode(mid.val);
        node.left = dfs(start, slow);
        node.right = dfs(slow.next, end);

        return node;
    }
