public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        if (headA == null || headB == null) {
            return null;
        }

        // pA ==> A + B = [1, 9, 1 ,2 ,4, 3, 2, 4]
        // pB ==> B + A = [3, 2, 4, 1, 9, 1, 2, 4]
        //                                   ^
        //           intersected node when pA == pB 

        ListNode pA = headA;
        ListNode pB = headB;
        while (pA != pB) {  // TC O(n+m)
            pA = pA != null ? pA.next : headB;
            pB = pB != null ? pB.next : headA;
        }

        return pA; // pA == null if two lists do not intersect
    }
}
