class LRUCache {
    // 双向链表，MRU节点在head之后，LRU节点在tail之前
    // head <-> MRU <-> ... <-> LRU <-> tail
    ListNode head;
    ListNode tail;

    HashMap<Integer, ListNode> cache; // key -> ListNode

    int cap;
    

    public LRUCache(int capacity) {
        head = new ListNode();
        tail = new ListNode();
        head.next = tail;
        tail.prev = head;
        cap = capacity;
        cache = new HashMap<>();
    }
    
    public int get(int key) {
        if (!cache.containsKey(key)) {
            return -1;
        }

        ListNode node = cache.get(key);
        moveToHead(node);
        return node.value;
    }
    
    public void put(int key, int value) {
        if (cache.containsKey(key)) {
            ListNode node = cache.get(key);
            moveToHead(node);
            node.value = value;
            return;
        }

        if (cache.size() == cap) {
            ListNode lruNode = tail.prev;
            unlink(lruNode);
            cache.remove(lruNode.key);
        }

        ListNode node = new ListNode(key, value);
        moveToHead(node);
        cache.put(key, node);
    }

    private void unlink(ListNode node) {
        ListNode prev = node.prev;
        ListNode next = node.next;
        if (prev != null) {
            prev.next = next;
        }
        if (next != null) {
            next.prev = prev;
        }
    }

    private void moveToHead(ListNode node) {
        unlink(node);
        
        ListNode headNext = head.next;
        head.next = node;
        node.prev = head;
        node.next = headNext;
        headNext.prev = node;
    }

    public class ListNode {
        ListNode prev;
        ListNode next;
        int key;
        int value;

        public ListNode(int key, int value) {
            this.key = key;
            this.value = value;
            this.prev = null;
            this.next = null;
        }

        public ListNode() {
            this(-2, -2);
        }
    }
}

