            if (colors[i] == 0) {
                if (!setColor(graph, i, colors, 1)) {
                    // 染色失败
                    return false;
                }
            }
        }

        // 染色成功
        return true;
    }

    private boolean setColor(List<Integer>[] graph, int node, int[] colors, int color) {
        colors[node] = color;
        for (int neibor : graph[node]) {
            if (colors[neibor] == color) {
                // 染色冲突
                return false;
            } else if (colors[neibor] == 0) {
                // DFS染色
                setColor(graph, neibor, colors, color * -1);
            } else {
                // 已经染色
            }
        }

        return true;
    }
