class Solution {
    public int[] getOrder(int[][] tasks) {
        // [enqueueTime, processingTime] 这个可以理解为一个时间的区间 [start = enqueueTime, end = start + processingTime]
        // 最后要求执行时间最短
        int n = tasks.length;
        TaskInfo[] taskInfo = new TaskInfo[n]; // SC O(n)
        for (int i=0; i<n; i++) { // TC O(n)
            taskInfo[i] = new TaskInfo(i, tasks[i]);
        }
        Arrays.sort(taskInfo, (t1, t2) -> (t1.startTime - t2.startTime)); // TC O(nlogn)
        
        // 贪心：每次可以执行任务时，选执行时间最短的，这样总时间最短
        PriorityQueue<TaskInfo> minHeap = new PriorityQueue<>((t1, t2) -> {
            // 先按 执行时间 排序，再按索引顺序排序
            if (t1.processingTime != t2.processingTime) {
                return t1.processingTime - t2.processingTime;
            } else {
                return t1.index - t2.index;
            }
        }); // SC O(n)
        
        int[] orders = new int[n]; // SC O(n)
        int orderIndex = 0;
        int taskIndex = 0;
        int usedTime = 1;
        while (orderIndex < n) { // TC O(n)
            while (taskIndex < n &&  taskInfo[taskIndex].startTime <= usedTime) {
                // 这些任务都可以执行了，加入队列 - enqueue
                minHeap.offer(taskInfo[taskIndex++]); // TC O(logn)
            }
            if (!minHeap.isEmpty()) {
                // 从队列中拿出一个任务进行处理
                // 选执行时间最短的(堆顶)
                TaskInfo curTask = minHeap.poll(); // TC O(1)
                orders[orderIndex++] = curTask.index;
                usedTime += curTask.processingTime;
            } else {
                // 没有任务，直接跳转到当前任务的开始时间
                usedTime = taskInfo[taskIndex].startTime;
            }
        }
        
        return orders;
    }
}

class TaskInfo {
    int index; // 因为最后要输出任务序号，所以这里需要把任务序号也记录一下，防止排序之后搞乱了
    int startTime;
    int processingTime;

    public TaskInfo(int index, int[] task) {
        this.index = index;
        this.startTime = task[0];
        this.processingTime = task[1];
    }
}
