var decodeString = function(s) {
    let stack = []
    let strStack = []
    let res = ''

    for(let i  = 0; i< s.length; i++){
        if( s[i] === ']'){
            let tmpStr = ''
            let char = ''
            for(let idx = stack.length-1; stack[idx] !== '['; idx-- ){
                let char = stack.pop()
                tmpStr = char + tmpStr
            }
            stack.pop();
            let num = ''
            while(!Number.isNaN(Number(stack.at(-1)))){
                num = stack.pop() + num
            }
            stack.push(tmpStr.repeat(Number(num)))
        }
        else {
            stack.push(s[i])
        }
    }
    return stack.toString().replaceAll(',','')
};
