2. 由于获取也要为 O(1), 所以用HashMap来保存key和对应node

### 代码
``` javascript

class DoubleLinkedListNode {
    constructor(key, value) {
        this.key = key
        this.value = value
        this.pre = null
        this.next = null
    }
}

class LRUCache{
    constructor(capacity){
        this.dummyHead = new DoubleLinkedListNode(null,null)
        this.dummyTail = new DoubleLinkedListNode(null, null)
        this.dummyHead.next = this.dummyTail
        this.dummyTail.pre = this.dummyHead
        this.hashMap = new Map()
        this.capacity = capacity
        this.size = 0

    }
    _addHead(key,val){
        let newNode = new DoubleLinkedListNode(key,val)
        this.dummyHead.next.pre = newNode
        newNode.next = this.dummyHead.next
        this.dummyHead.next = newNode
        newNode.pre = this.dummyHead
        this.size++
        this.hashMap.set(key,newNode)
    }
    _deleteNode(node){
        node.pre.next = node.next
        node.next.pre = node.pre
        node.next = null
        node.pre = null
        this.size--
        this.hashMap.delete(node.key)
    }
    _deleteTail(){
        this._deleteNode(this.dummyTail.pre)
    }
    get(key){
        let node = this.hashMap.get(key)
        if(!node){
            return -1
        }
        this._deleteNode(node)
        this._addHead(node.key, node.value)
        return node.value
    }
    put(key,val){
        let old = this.hashMap.get(key)
        if(old){
            this._deleteNode(old)
            this._addHead(key, val)
            return 
        }
        if(this.size === this.capacity){
            this._deleteTail()
        }
        this._addHead(key, val)
    }
}

