class Solution:
    def mySqrt(self, x:int) -> int:
        if x < 2:
            return x

        left, right = 0, x // 2 + 1
        ans = 0

        while left <= right:
            mid = (left + right) // 2
            mid_sq = mid * mid

            if mid_sq == x:
                return mid

            elif mid_sq < x:
                ans = mid
                left = mid + 1

            else:
                right = mid - 1

        return ans



