ListNode *detectCycle(ListNode *head) 
{
    ListNode *fast = head, *slow = head;
    while (fast && fast->next) 
   {
        slow = slow->next;
        fast = fast->next->next;
        if (slow == fast) 
        { 
            ListNode *f = fast, *h = head;
            while (f != h) f = f->next, h = h->next;
            return h;
        }
    }
    return NULL;
}
