void pre_traving(struct TreeNode* root, int* i, int* max, int* min, int nums[][3], int row, int col)//先序遍历二叉树，并将结点的信息都存入nums数组
{
    if(root)
    {
        *max=(*max>col)?*max:col;
        *min=(*min<col)?*min:col;
        nums[*i][0]=root->val;
        nums[*i][1]=row;
        nums[*i][2]=col;
        ++(*i);
        pre_traving(root->left, i, max, min, nums, row+1, col-1);
        pre_traving(root->right, i, max, min, nums, row+1, col+1);
    }
}

void sort(int temp[][2], int n)//插入排序
{
    int i,j,t[2];
    for(i=1; i<n; i++)
    {
        t[0]=temp[i][0],t[1]=temp[i][1];
        for(j=i; j>=1; j--)
        {
            if(temp[j-1][1]>t[1])
            {
                temp[j][0]=temp[j-1][0];
                temp[j][1]=temp[j-1][1];
            }
            else if(temp[j-1][1]==t[1]&&temp[j-1][0]>t[0])
            {
                temp[j][0]=temp[j-1][0];
                temp[j][1]=temp[j-1][1];
            }
            else break;    
        }
        temp[j][0]=t[0];
        temp[j][1]=t[1];
    }
}

int** verticalTraversal(struct TreeNode* root, int* returnSize, int** returnColumnSizes){
    int nums[1000][3];
    int temp[500][2];
    int max=0,min=0,tail=0,i,j,k,lenth;//max和min分别表示最小和最大列
    pre_traving(root, &tail, &max, &min, nums, 0, 0);
    *returnSize=max-min+1;
    *returnColumnSizes=(int*)malloc(sizeof(int)*(*returnSize));
    int** answer=(int**)malloc(sizeof(int*)*(*returnSize));
    for(j=0,i=min; i<=max; j++,i++)
    {
        lenth=0;
        for(k=0; k<tail; k++)
        {
            if(nums[k][2]==i)
            {
                temp[lenth][0]=nums[k][0];
                temp[lenth++][1]=nums[k][1];
            }
        }
        (*returnColumnSizes)[j]=lenth;
        sort(temp, (*returnColumnSizes)[j]);
        answer[j]=(int*)malloc(sizeof(int)*(*returnColumnSizes)[j]);
        for(k=0; k<(*returnColumnSizes)[j]; k++)
            answer[j][k]=temp[k][0];
    }
    return answer;
}
