#include <vector>

class CustomStack {
private:
    std::vector<int> stack;
    int maxSize;

public:
    CustomStack(int maxSize) {
        this->maxSize = maxSize;
    }

    void push(int x) {
        if (stack.size() < maxSize) {
            stack.push_back(x);
        }
    }

    int pop() {
        if (!stack.empty()) {
            int top = stack.back();
            stack.pop_back();
            return top;
        } else {
            return -1;
        }
    }

    void increment(int k, int val) {
        for (int i = 0; i < k && i < stack.size(); i++) {
            stack[i] += val;
        }
    }
};

#include <stack>
#include <string>

class Solution {
public:
    string decodeString(string s) {
        std::stack<int> counts;
        std::stack<std::string> strings;
        std::string currentString;
        int count = 0;

        for (char ch : s) {
            if (isdigit(ch)) {
                count = count * 10 + ch - '0';
            } else if (ch == '[') {
                counts.push(count);
                strings.push(currentString);
                count = 0;
                currentString.clear();
            } else if (ch == ']') {
                std::string decodedString = strings.top();
                strings.pop();
                int repeatCount = counts.top();
                counts.pop();
                for (int i = 0; i < repeatCount; i++) {
                    decodedString += currentString;
                }
                currentString = decodedString;
            } else {
                currentString += ch;
            }
        }

        return currentString;
    }
};

struct ListNode {
    int val;
    ListNode *next;
    ListNode(int x) : val(x), next(NULL) {}
};

class Solution {
public:
    ListNode* swapPairs(ListNode* head) {
        ListNode dummy(0);
        dummy.next = head;
        ListNode* prev = &dummy;
        while (head && head->next) {
            ListNode* first = head;
            ListNode* second = head->next;

            // 交换两个节点
            first->next = second->next;
            second->next = first;
            prev->next = second;

            // 移动到下一对节点
            prev = first;
            head = first->next;
        }
        return dummy.next;
    }
};

