    TreeNode *right;
    TreeNode(int x) : val(x), left(NULL), right(NULL) {}
};

int findBottomLeftValue(TreeNode* root) {
    std::queue<TreeNode*> q;
    q.push(root);
    int result = root->val;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; ++i) {
            TreeNode* node = q.front();
            q.pop();
            // 对于每一层的第一个元素，更新结果
            if (i == 0) {
