                q.push(node->left);
            }
            if (node->right) {
                q.push(node->right);
            }
        }
    }
    return result;
}

#include <algorithm>
#include <iostream>
#include <vector>

std::vector<int> solution(std::vector<int> &nums, int target) {
  // 对数组进行排序
  std::sort(nums.begin(), nums.end());

  // 初始化两个指针
  int left = 0;
  int right = nums.size() - 1;

  // 使用双指针遍历数组
  while (left < right) {
    int sum = nums[left] + nums[right];

    if (sum == target) {
      // 找到目标值，返回两个指针的下标
      return {left, right};
    } else if (sum < target) {
      // 和小于目标值，移动左指针
      ++left;
    } else {
      // 和大于目标值，移动右指针
      --right;
    }
  }

  // 如果没有找到，返回空数组（题目保证有解，所以这里不会执行）
  return {};
}

