var shortestToChar = function(s, c) {
  const n = s.length;
  const prev = [];
  let lastPos = -n;

  // 从左到右遍历,记录上一次出现 c 的位置
  for (let i = 0; i < n; i++) {
      if (s[i] === c) {
          lastPos = i;
      }
      prev[i] = i - lastPos;
  }

  lastPos = 2 * n;
  // 从右到左遍历,记录下一次出现 c 的位置
  for (let i = n - 1; i >= 0; i--) {
      if (s[i] === c) {
          lastPos = i;
      }
      prev[i] = Math.min(prev[i], lastPos - i);
  }

  return prev;
};
