var decodeString = function(s) {
    let result = '';
    let num = 0;

    for (let i = 0; i < s.length; i++) {
        const char = s[i];

        if (!isNaN(parseInt(char))) {
            num = num * 10 + parseInt(char);
        } else if (char === '[') {
            let j = i;
            let count = 1;
            while (count !== 0) {
                j++;
                if (s[j] === '[') count++;
                if (s[j] === ']') count--;
            }
            const innerString = decodeString(s.slice(i + 1, j));
            result += innerString.repeat(num);
            i = j;
            num = 0;
        } else if (char !== ']') {
            result += char;
        }
    }

    return result;
};
