/**
 * Definition for singly-linked list.
 * function ListNode(val, next) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.next = (next===undefined ? null : next)
 * }
 */
/**
 * @param {ListNode} head
 * @param {number} k
 * @return {ListNode}
 */
var rotateRight = function(head, k) {
    if (!head || !head.next) return head; // 空链表或只有一个节点,直接返回

    // 1. 计算链表长度
    let n = 0, curr = head;
    while (curr) {
        n++;
        curr = curr.next;
    }

    // 2. 计算实际需要旋转的次数
    k = k % n;
    if (k === 0) return head; // 不需要旋转

    // 3. 找到倒数第 k' 个节点
    curr = head;
    for (let i = 0; i < n - k - 1; i++) {
        curr = curr.next;
    }
    let newHead = curr.next;

    // 4. 形成环
    curr.next = null;
    curr = newHead;
    while (curr.next) {
        curr = curr.next;
    }
    curr.next = head;

    // 5. 返回新的头节点
    return newHead;
};
```
