 * @return {ListNode}
 */
var swapPairs = function(head) {
    // 如果链表为空或只有一个节点,直接返回
    if (!head || !head.next) {
        return head;
    }

    // 创建一个虚拟头节点,方便处理边界条件
    let dummy = new ListNode(0);
    dummy.next = head;

    // 三个指针:prev用于记录上一个节点,curr用于当前节点,nextNode用于记录下一个节点
    let prev = dummy;
    let curr = head;

    // 遍历链表,每次处理两个节点
    while (curr && curr.next) {
        // 保存下一个节点的地址
        let nextNode = curr.next.next;

        // 交换当前两个节点
        prev.next = curr.next;
        curr.next.next = curr;
        curr.next = nextNode;

        // 移动三个指针到下一个要处理的节点对
        prev = curr;
        curr = nextNode;
    }

    // 返回交换后的链表头部
    return dummy.next;
};
