/**
 * @param {ListNode} head - 输入的有序链表头节点
 * @return {TreeNode} - 返回构造的平衡二叉搜索树的根节点
 */
var sortedListToBST = function(head) {
    // 处理基础情况
    // 如果链表为空，返回空节点
    if (!head) return null;
    // 如果只有一个节点，直接返回树节点
    if (!head.next) return new TreeNode(head.val);

    // 使用快慢指针找到链表的中间节点
    let slow = head;        // 慢指针，每次移动一步
    let fast = head;        // 快指针，每次移动两步
    let prev = null;        // 记录慢指针的前一个节点

    // 当快指针能够继续移动时，移动快慢指针
    while (fast && fast.next) {
        fast = fast.next.next;
        prev = slow;
        slow = slow.next;
    }

    // 在中间节点处断开链表，分成左右两部分
    prev.next = null;

    // 用中间节点的值创建根节点
    const root = new TreeNode(slow.val);

    // 递归构建左右子树
    // 左子树由链表的前半部分构建
    root.left = sortedListToBST(head);
    // 右子树由链表的后半部分构建
    root.right = sortedListToBST(slow.next);

    return root;
};
