 * @return {ListNode}
 */
var detectCycle = function(head) {
    if (!head || !head.next) return null;

    // 1. 使用快慢指针找相遇点
    let slow = head;
    let fast = head;
    let hasCycle = false;

    while (fast && fast.next) {
        slow = slow.next;
        fast = fast.next.next;
        if (slow === fast) {
            hasCycle = true;
            break;
        }
    }

    // 如果没有环，返回 null
    if (!hasCycle) return null;

    // 2. 一个指针回到起点，两指针同速前进找入口
    slow = head;
    while (slow !== fast) {
        slow = slow.next;
        fast = fast.next;
    }

    return slow;
};
