var sumNumbers = function(root) {
    // 边界情况：空树
    if (!root) return 0;

    // DFS遍历的辅助函数
    function dfs(node, currentSum) {
        // 基础情况：空节点
        if (!node) return 0;

        // 计算当前数字：将当前数字乘10并加上当前节点值
        const newSum = currentSum * 10 + node.val;

        // 如果是叶子节点，返回当前路径和
        if (!node.left && !node.right) {
            return newSum;
        }

        // 递归计算左右子树的和并相加
        return dfs(node.left, newSum) + dfs(node.right, newSum);
    }

    // 从根节点开始DFS，初始和为0
    return dfs(root, 0);
};
