var topKFrequent = function(nums, k) {
    // 统计每个数字出现的频率
    const map = new Map();
    for (let i = 0; i < nums.length; i++) {
        const num = nums[i];
        map.set(num, map.get(num) ? map.get(num) + 1 : 1)
    }

    // 索引为数字出现的次数 值为该次数的数字数组
    const arr = []
    for (let i = 0; i <= nums.length; i++) {
        arr.push([])
    }
    for (const [num, times] of map) {
        arr[times].push(num)
    }

    // 出现次数从高到低的数字的数组
    const res = []
    for (let i = arr.length - 1; i > 0; i--) {
        res.push(...arr[i])
    }
    // 返回前K项
    return res.slice(0, k)
};
