/**
 * @param {string} s
 * @param {string[]} words
 * @return {number[]}
 */
function findSubstring(s, words) {
    if (!s || !words || words.length === 0) return [];

    const result = [];
    const wordLen = words[0].length;
    const totalLen = wordLen * words.length;
    const wordCount = new Map();

    // 统计每个单词出现的次数
    for (const word of words) {
        wordCount.set(word, (wordCount.get(word) || 0) + 1);
    }

    // 对每个可能的起始位置进行检查
    for (let i = 0; i <= s.length - totalLen; i++) {
        const seen = new Map();
        let j = 0;

        // 检查从位置i开始的子串
        while (j < words.length) {
            const start = i + j * wordLen;
            const currentWord = s.slice(start, start + wordLen);

            // 如果当前单词不在words中，直接退出
            if (!wordCount.has(currentWord)) break;

            // 更新当前看到的单词计数
            seen.set(currentWord, (seen.get(currentWord) || 0) + 1);

            // 如果当前单词出现次数超过了允许的次数，退出
            if (seen.get(currentWord) > wordCount.get(currentWord)) break;

            j++;
        }

        // 如果成功匹配了所有单词
        if (j === words.length) {
            result.push(i);
        }
    }

    return result;
}
