var maxSlidingWindow = function(nums, k) {
    const deque = [];
    const result = [];

    for(let i = 0; i < nums.length; i++) {
        // 移除队首过期元素
        if(deque.length && deque[0] <= i - k) {
            deque.shift();
        }

        // 移除队尾小于当前元素的值
        while(deque.length && nums[deque[deque.length - 1]] < nums[i]) {
            deque.pop();
        }

        deque.push(i);

        // 当窗口至少为k时,记录最大值
        if(i >= k - 1) {
            result.push(nums[deque[0]]);
        }
    }

    return result;
};
