 * @param {string} moves - 移动指令字符串
 * @return {boolean} - 是否返回原点
 */
var judgeCircle = function(moves) {
    // 初始化水平和垂直坐标
    let x = 0, y = 0;

    // 遍历每个移动指令
    for (let move of moves) {
        switch (move) {
            case 'U': // 向上移动
                y++;
                break;
            case 'D': // 向下移动
                y--;
                break;
            case 'R': // 向右移动
                x++;
                break;
            case 'L': // 向左移动
                x--;
                break;
        }
    }

    // 检查是否回到原点
    return x === 0 && y === 0;
};

