/**
 * @param {string} a
 * @param {string} b
 * @return {number}
 * 时间复杂度: O(n)
 * 空间复杂度: O(1)
 */
var minCharacters = function(a, b) {
    // 创建两个长度为26的数组存储字符频次
    const countA = new Array(26).fill(0);
    const countB = new Array(26).fill(0);

    // 统计两个字符串中每个字符出现的频次
    for (let c of a) countA[c.charCodeAt(0) - 97]++;
    for (let c of b) countB[c.charCodeAt(0) - 97]++;

    // 初始化结果为最大可能值
    let res = a.length + b.length;
    let sumA = 0, sumB = 0;

    // 遍历所有可能的分割点(a-y)，检查条件1和2
    for (let i = 0; i < 25; i++) {
        sumA += countA[i];
        sumB += countB[i];

        // 条件1：使字符串a中所有字符严格小于字符串b中的所有字符
        res = Math.min(res, sumA + (b.length - sumB));

        // 条件2：使字符串b中所有字符严格小于字符串a中的所有字符
        res = Math.min(res, sumB + (a.length - sumA));
    }

    // 条件3：使两个字符串中的所有字符相等
    for (let i = 0; i < 26; i++) {
        res = Math.min(res, a.length + b.length - countA[i] - countB[i]);
    }

    return res;
};
