class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
        res = []
        q = deque()

        for idx, num in enumerate(nums):
            while q and q[-1] < num:
                q.pop()
            q.append(num)

            if idx >= k and nums[idx - k] == q[0]:
                q.popleft()

            if idx >= k - 1:
                res.append(q[0])

        return res

时间复杂度: O(n)
空间复杂度: O(k)

class Solution:
    def findJudge(self, n: int, trust: List[List[int]]) -> int:
        trusted = [0] * (n+1)
        for (a,b) in trust:
            trusted[a] -= 1
            trusted[b] += 1
        for i in range(1, len(trusted)):
            if trusted[i] == n-1:
                return i
        return -1

