class Solution:
    def possibleBipartition(self, n: int, dislikes: List[List[int]]) -> bool:
        g = defaultdict(list)

        for x,y in dislikes:
            g[x].append(y)
            g[y].append(x)

        color = {}

        def dfs(i,c):
            if i in color: return color[i] == c
            color[i] = c
            for enemy in g[i]:
                if not dfs(enemy, 1-c):  # Check all neighbors, return False if any conflict
                    return False
            return True

        for i in range(1, n+1):
            if i not in color:
                 if not dfs(i, 1):  # Start a DFS for unvisited nodes
                    return False

        return True

class Solution:
    def mySqrt(self, x: int) -> int:
        if x == 0: return 0
        left, right = 1, x
        while left <= right:
            mid = (left + right)//2
            if mid * mid == x: 
                return mid
            elif mid * mid < x: 
                left = mid + 1
            else: 
                right = mid - 1
        return right

class Solution:
    def firstBadVersion(self, n: int) -> int:
        left, right = 0, n
        while left <= right:
            mid = (right + left)//2
            if isBadVersion(mid):
                right = mid - 1
            else:
                left = mid + 1
        return left

class Solution:
    def swimInWater(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])

        if n == m == 1:
            return grid[0][0]

        def get_nexts(i, j, visited):
            for x, y in ((i - 1, j),
                         (i + 1, j),
                         (i, j - 1),
                         (i, j + 1)):
                if (
                    0 <= x < n
                    and 0 <= y < m
                    and (x, y) not in visited
                ):
                    yield (x, y)

        def check(value):
            q = [(0, 0)]
            visited = {(0, 0)}
            while q:
                i, j = q.pop()
                for x, y in get_nexts(i, j, visited):
                    if grid[x][y] > value:
                        continue
                    if x == n - 1 and y == m - 1:
                        return True
                    q.append((x, y))
                    visited.add((x, y))
            return False

        left = max(grid[0][0], grid[-1][-1])
        right = max(max(line) for line in grid)

        if check(left):
            return left

        while right - left > 1:
            middle = (left + right) // 2
            if check(middle):
                right = middle
            else:
                left = middle

        return right

