class Solution:
    def decodeString(self, s: str) -> str:
        res = ""
        stack = []
        num = 0
        for item in s:
            if item.isdigit():
                num = num * 10 + int(item)
            elif item == "[":
                stack.append([num, res])
                num = 0
                res = ""
            elif item == "]":
                pre_num, pre_res = stack.pop()
                res = pre_res + pre_num * res
            else:
                res += item
        return res
