var findBottomLeftValue = function(root) {
    let left = 0,ans;
    dfs(root,0)
    return ans;
    // ----------------------
    function dfs(root,level){
        if(!root) return;
        if(level === left) {
            left++;
            ans = root.val;
        }
        dfs(root.left,level + 1);
        dfs(root.right,level + 1);
    }
};
