// Time Complexity: O(n);
// Space Complexity: O(1);

var rotateRight = function (head, k) {
  if (!head || !head.next) return head;

  let length = 0;
  let cur = head;

  while (cur) {
    cur = cur.next;
    length += 1;
  }

  k = k % length;
  let left = head;
  let right = head;

  while (right.next) {
    if (k <= 0) left = left.next;
    right = right.next;
    k -= 1;
  }

  right.next = head; // Handle the case when left and right point to the same node.
  let res = left.next;
  left.next = null;

  return res;
};
