var detectCycle = function(head) {
    let fast = head;
    let slow = head;

    do {
        if (fast === null) return null;

        fast = fast?.next?.next ?? null;
        slow = slow?.next ?? null;
    } while (fast !== slow);

    fast = head;

    while (fast !== slow) {
        fast = fast?.next ?? null;
        slow = slow?.next ?? null;
    }

    return fast;
};
