若是 put 更新既有的值要 O(1)，還是需要使用鏈表。
- 中間有一度 debug 卡關，主要是原本在 put 時無論如何都先把既有的 node 刪掉再新增，但這可能造成一些 state 更新上的錯誤，後來簡化為直接移動 node 才 AC。

```
// Time Complexity: O(1) for LRUCache.prototype.get, put
// Space Complexity: O(n)

function ListNode(key, val) {
    this.key = key;
    this.val = val;
    this.next = null;
    this.pre = null;
 }

/**
 * @param {number} capacity
 */
var LRUCache = function(capacity) {
    this.capacity = capacity;
    this.length = 0;
    this.head = new ListNode();
    this.tail = new ListNode();
    this.head.next = this.tail;
    this.tail.pre = this.head;
    this.map = {};
};

/** 
 * @param {number} key
 * @return {number}
 */
LRUCache.prototype.get = function(key) {
    const node = this.map[key];

    if (node !== undefined) {
        this.appendToTail(this.remove(node));
        return node.val
    };

    return -1;
};

/** 
 * @param {number} key 
 * @param {number} val
 * @return {void}
 */
LRUCache.prototype.put = function(key, val) {
    const node = this.map[key];

    if (node !== undefined) {
        node.val = val;
        this.appendToTail(this.remove(node));
    } else {
        if (this.length === this.capacity) {
            const old = this.head.next;
            delete this.map[old.key];
            this.length -= 1;
            this.remove(old);
        }

        const node = new ListNode(key, val);
        this.map[key] = node;
        this.length += 1;
        this.appendToTail(node);
    }
};

LRUCache.prototype.appendToTail = function(node) {
    if (node.pre) node.pre.next = node.next;
    if (node.next) node.next.pre = node.pre;
    this.tail.pre.next = node;
    node.pre = this.tail.pre;
    node.next = this.tail;
    this.tail.pre = node;
}

LRUCache.prototype.remove = function(node) {
    node.pre.next = node.next;
    node.next.pre = node.pre;
    node.pre = null;
    node.next = null;

    return node;
}

/** 
 * Your LRUCache object will be instantiated and called as such:
 * var obj = new LRUCache(capacity)
 * var param_1 = obj.get(key)
 * obj.put(key,value)
 */
```

