```javascript
// Time Complexity: O(n), n is the number of the tree nodes
// Space Complexity: O(d), d is the depth of the tree

/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
var sumNumbers = function(root) {
    let sum = 0;
    let runningNumber = 0;

    const inner = (root) => {
        if (root) {
            runningNumber = runningNumber * 10 + root.val;

            if (!root.left && !root.right) {
                sum += runningNumber;
            } else {
                inner(root.left);
                inner(root.right);
            }

            runningNumber = Math.floor(runningNumber / 10);
        }
    };

    inner(root);

    return sum;
};
```

