思路二使用一个 hashmap 维护自增量，只有在 pop 操作的时候才去运算：

```js
/**
 * @param {number} maxSize
 */
var CustomStack = function(maxSize) {
    this.stack = []
    this.size = 0
    this.maxSize = maxSize
    this.map = {}
};

/** 
 * @param {number} x
 * @return {void}
 */
CustomStack.prototype.push = function(x) {
    if (this.size === this.maxSize) return;
    this.stack.push(x);
    this.size ++
};

/**
 * @return {number}
 */
CustomStack.prototype.pop = function() {
    if (this.size === 0) return -1
    const inc = this.map[this.size - 1] || 0;
    const res = this.stack.pop();
    // update inc
    if (inc) {
        this.map[this.size - 1] = 0;
        this.map[this.size - 2] = this.map[this.size - 2] ? this.map[this.size - 2] + inc : inc;
    }
    this.size --

    return res + inc;
};

/** 
 * @param {number} k 
 * @param {number} val
 * @return {void}
 */
CustomStack.prototype.increment = function(k, val) {
    const key = Math.min(k - 1, this.size - 1);
    this.map[key] = this.map[key] ? this.map[key] + val : val;
};

/** 
