```js
/**
 * @param {string} s
 * @return {string}
 */
var decodeString = function(s) {
    const stack = [];
    let i = 0;
    while(i < s.length) {
        const char = s[i];
        if (char !== ']') {
            stack.push(char)
            i ++
        } else {
            let repeatStr = ''
            while(stack[stack.length - 1] !== '[') {
                const new_char = stack.pop();
                repeatStr = new_char + repeatStr;
            }
            // pop '['
            stack.pop()
            // get number 
            let repeat_times = ''
            while(/[0-9]/.test(stack[stack.length - 1])) {
                repeat_times = stack.pop() + repeat_times
            }
            repeat_times = Number(repeat_times)
            stack.push(repeatStr.repeat(repeat_times));
            i ++
        }
    }
    let res = ''
    while(stack.length) {
        res = stack.pop() + res;
    }
    return res
};
