```js
var rotateRight = function(head, k) {
  if (!head || !head.next || k === 0) return head;

  // Count the length of the list
  let length = 1;
  let tail = head;
  while (tail.next) {
    tail = tail.next;
    length++;
  }

  // Normalize k
  k %= length;
  if (k === 0) return head;

  // Find the new tail: (length - k - 1)th node
  let newTail = head;
  for (let i = 0; i < length - k - 1; i++) {
    newTail = newTail.next;
  }

  // Rotate
  let newHead = newTail.next;
  newTail.next = null;
  tail.next = head;

  return newHead;
};

```

