var detectCycle = function(head) {
    if (!head) return head;
    let fast = head, slow = head;
    // step1: find the intersection node
    while(fast && fast.next) {
        fast = fast.next.next;
        slow = slow.next;
        // step2: find the entry of the cycle
        if (fast === slow) {
            slow = head;
            while(fast !== slow) {
                fast = fast.next
                slow = slow.next
