class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer[]
     */
    function topKFrequent($nums, $k) {
        $hashtable = [];

        foreach ($nums as $val) {
            if (!isset($hashtable[$val])) {
                $hashtable[$val] = 1;
            }else {
                $hashtable[$val] += 1;
