class Solution {

    /**
     * @param Integer $n
     * @param Integer[][] $trust
     * @return Integer
     */
    function findJudge($n, $trust) {
        $inDegress = [];
        $outDegress = [];

        foreach ($trust as $val) {
            $inDegress[$val[1]]++;
            $outDegress[$val[0]]++;
        }

//        var_dump($inDegress, $outDegress );die;

        for ($i = 1; $i <= $n; $i++) {
            if ($inDegress[$i] == $n - 1 && $outDegress[$i] == 0) {
                return $i;
            }
        }

        return -1;
    }
}
