    def topKFrequent(self, nums: List[int], k: int) -> List[int]:
        heap = []
        freq = collections.Counter(nums)
        for f in freq:
            if len(heap) < k:
                heapq.heappush(heap, (freq[f], f))
            else:
                if freq[f] > heap[0][0]:
                    heapq.heappop(heap)
                    heapq.heappush(heap, (freq[f], f))
        return [h[1] for h in heap]
