```java
class CustomStack {
    private int[] stack;
    private int tt;
    public CustomStack(int maxSize) {
        stack = new int[maxSize];
    }

    public void push(int x) {
        if(tt == stack.length) return;
        stack[tt++] = x;
    }

    public int pop() {
        if(tt == 0) return -1;
        return stack[--tt];
    }

    public void increment(int k, int val) {
        for(int i = 0; i < k && i < tt; i++) {
            stack[i] += val;
        }
    }
}
