class Solution {
    public int maxChunksToSorted(int[] arr) {
        if(arr.length == 0 || arr == null){
            return 0;
        }
        LinkedList<Integer> stack = new LinkedList<>();
        for(int i = 0; i < arr.length; i++){
            int temp  = -1;
            while(!stack.isEmpty() && stack.getLast() > arr[i]){
                temp = Math.max(temp,stack.getLast());
                stack.removeLast();
            }
            if(temp !=-1){
                stack.add(temp);
            }else{
                stack.add(arr[i]);
            }
        }
        return stack.size();      
    }
}
