    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        if(headA == null || headB == null) return null;

        ListNode ha = headA;
        ListNode hb = headB;

        while(ha != hb) {
            if(ha == null && hb == null) return null;
            ha = ha == null ? headB:ha.next;
            hb = hb == null ? headA:hb.next;
