public class Solution {
    public ListNode detectCycle(ListNode head) {
        if(head == null || head.next == null) return null;

        ListNode s = head;
        ListNode f = head.next;

        while(f != null) {
            s = s.next;
            f = f.next;
            if(f == null) return null;
            f = f.next;
            if(s == f) {
                s = head;
                f = f.next;
                while(s != f) {
                    s = s.next;
                    f = f.next;
                }
                return s;
            }
        }
        return null;

    }
}
