```java
class Solution {
    public int numberOfBoomerangs(int[][] points) {
        int ret = 0 ;
        int n = points.length ;        
        for (int i = 0 ; i < n ; ++i) {
            HashMap<Integer,Integer> map = new HashMap<>();
            for (int j = 0 ; j < n ; ++j) {
               if (j == i) continue ;
               int d = get(points[i], points[j]) ;
               int cnt = map.getOrDefault(d, 0) + 1 ;
               map.put(d, cnt) ;
            }
            for (int k : map.keySet()) {
                int val = map.get(k) ;
                if (val >= 2) {
                    ret += val * (val - 1)  ;
                }
            }
        }
        return ret ;        
    }

    int get (int [] a, int [] b){
        int d1 = a[0] - b[0] ;
        int d2 = a[1] - b[1] ;
        return d1 * d1 + d2 * d2 ;

    }
}
