# 方法一：
class Solution:
    def twoSum(self, nums: List[int], target: int) -> List[int]:
        res = []
        if not nums:
            return res
        for i in range(len(nums)):
            if target - nums[i] in nums:
                j = nums.index(target - nums[i])
                if j != i:
